mapper.select:
select 

from user_retention_source_ltv
where id = #{id,jdbcType=BIGINT}

mapper.insert:
insert into user_retention_source_ltv (id, snid, gameid, 
  source, install_day, stat_day, 
  install, retention_day, retention_rate, 
  retention_num, total_amount, create_time
  )
values (#{id,jdbcType=BIGINT}, #{snid,jdbcType=VARCHAR}, #{gameid,jdbcType=VARCHAR}, 
  #{source,jdbcType=VARCHAR}, #{installDay,jdbcType=VARCHAR}, #{statDay,jdbcType=VARCHAR}, 
  #{install,jdbcType=INTEGER}, #{retentionDay,jdbcType=INTEGER}, #{retentionRate,jdbcType=DOUBLE}, 
  #{retentionNum,jdbcType=INTEGER}, #{totalAmount,jdbcType=DOUBLE}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select b.retention_day,avg(b.total_amount/b.install) as avg_ltv from (select * from user_retention_source_ltv
and ((retention_day >= 0 and retention_day <= 7) or retention_day = 14 or retention_day = 21 or retention_day = 30)
)b group by b.retention_day order by b.retention_day

select b.retention_day,avg(b.retention_rate) as avg_retention_rate from (select * from user_retention_source_ltv
and ((retention_day >= 0 and retention_day <= 7) or retention_day = 14 or retention_day = 21 or retention_day = 30)
)b group by b.retention_day order by b.retention_day

select a.retention_day,avg(a.retention_num / b.role_choice) as avg_retention_rate from (select * from user_retention_source_ltv
and ((retention_day >= 0 and retention_day <= 7) or retention_day = 14 or retention_day = 21 or retention_day = 30)
)a left outer join ( select day,role_choice from source_daily_report
) b on a.install_day = b.day group by a.retention_day order by a.retention_day

